'use client'

import React from 'react'

const Button = () => {
  return (
    <div>
      <button 
        className='bg-red-500 rounded-md px-4 py-2' 
        onClick={() => {
          const body = document.querySelector('body');
          if (body) {
            body.style.backgroundColor = 'red';
          }
        }}
      >
        Click
      </button>
    </div>
  )
}

export default Button
